import React, { useState, useEffect, createContext, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom'; 

const LOCAL_STORAGE_AUTH_TOKEN_KEY = 'landslide_app_auth_token';

export const AuthContext = createContext(null);

const AuthPage = ({ children }) => {
  const [user, setUser] = useState(null); 
  const [user_id, setUser_id] = useState(null);
  const [token, setToken] = useState(null); 

  const [isLoadingAuth, setIsLoadingAuth] = useState(true); 

  const [authError, setAuthError] = useState('');
  const [message, setMessage] = useState('');

  const [inputEmail, setInputEmail] = useState('');
  const [inputPassword, setInputPassword] = useState('');
  const [inputUsername, setInputUsername] = useState('');

  const API_BASE_URL = 'http://127.0.0.1:8000'; 

  const navigate = useNavigate();

  const applyAuthData = useCallback((authToken, userData = null) => {
      setAuthError(''); 
      setMessage('');   

      if (authToken && userData) {
          localStorage.setItem(LOCAL_STORAGE_AUTH_TOKEN_KEY, authToken);
          setToken(authToken);
          setUser({ email: userData.email, user_id: userData.user_id, username: userData.username });
          setUser_id(userData.user_id);
      } else {
          localStorage.removeItem(LOCAL_STORAGE_AUTH_TOKEN_KEY);
          setToken(null);
          setUser(null);
          setUser_id(null); 
      }
  }, []); 

  const fetchUserFromToken = useCallback(async (authToken) => {
      if (!authToken) {
          applyAuthData(null); 
          setIsLoadingAuth(false);
          return;
      }

      try {
          const response = await fetch(`${API_BASE_URL}/users/me`, {
              method: 'GET',
              headers: {
                  'Authorization': `Bearer ${authToken}`, 
                  'Content-Type': 'application/json',
              },
          });
          //I dont think this is nesssacery 
           if (response.ok) {
                const data = await response.json();
                if (data && typeof data.user_id === 'string' && typeof data.user_email === 'string') {
                    applyAuthData(authToken, { user_id: data.user_id, email: data.user_email, username: data.username });
                } else {
                    console.error("DEBUG: /users/me response missing expected 'user_id' or 'user_email' or wrong type:", data);
                    applyAuthData(null);
                }
            }
            else {
              const errorData = await response.json();
              console.error("DEBUG: /users/me fetch failed. Status:", response.status, "Details:", errorData);
              applyAuthData(null); 
          }
      } catch (error) {
          console.error('DEBUG: Network error during /users/me fetch:', error);
          applyAuthData(null); 
      } finally {
          setIsLoadingAuth(false); 
      }
  }, [API_BASE_URL, applyAuthData]); 

  useEffect(() => {
    const storedToken = localStorage.getItem(LOCAL_STORAGE_AUTH_TOKEN_KEY);

    if (storedToken) {
        fetchUserFromToken(storedToken);
    } else {
        setIsLoadingAuth(false);
    }
  }, [fetchUserFromToken]); 

  const handleSignInInternal = useCallback(async (email, password) => {
    setAuthError('');
    setMessage('');

    try {
        const details = new URLSearchParams();
        details.append('email', email);
        details.append('password', password);

        const response = await fetch(`${API_BASE_URL}/token`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
            body: details.toString(),
        });

        if (response.ok) {
            const data = await response.json();
            // Apply auth data with user details received from the token endpoint
            applyAuthData(data.access_token, { user_id: data.user_id, email: data.user_email, username: data.username });
            setMessage('Logged in successfully!');
            setInputEmail('');
            setInputPassword('');
            setInputUsername(''); // Clear username input after successful login/signup
            navigate('/'); // Navigate to home page on success
            return true;
        } else {
            const errorData = await response.json();
            setAuthError(errorData.detail || 'Invalid email or password.');
            return false;
        }
    } catch (error) {
        console.error('Sign in network error:', error);
        setAuthError('Network error. Please try again.');
        return false;
    }
  }, [API_BASE_URL, applyAuthData, navigate]);

  // const handleSignInInternal = useCallback(async (signInEmail, signInPassword, signInUsername) => {
  //   setAuthError('');
  //   setMessage('');
  //   if ((!signInEmail || !signInUsername)|| !signInPassword) {
