import React, { useState, useEffect, useMemo} from 'react'
import { MapContainer, TileLayer, useMap, GeoJSON} from 'react-leaflet'
import 'leaflet/dist/leaflet.css';


// get query imports (just query the whole database)
// return the points displayed on the map
// for loop to get each one on
// small circle w blue
// popup wiht info from db
const center = [38.6263, -90.1751]
const zoom = 10

function MapContent({ setMapInstance }) {
  const map = useMap(); 
  useEffect(() => {
    setMapInstance(map); 
  }, [map, setMapInstance]);

  return null;
}

export default function MapCoords(){ 
    const [map, setMap] = useState(new Map());
    const [geoJsonData, setGeoJsonData] = useState(null);

    useEffect(() => {
    const fetchPoints = async () => {
      try {
        // Replace with your actual API endpoint
        const response = await fetch('http://127.0.0.1:8000/docs#/default/query_data_imports_query_data_imports__get'); // Example API endpoint
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();

        const featureCollection = {
          type: 'FeatureCollection',
          features: data.map(item => ({
            type: 'Feature',
            properties: {
              landslideid: item.landslideid,
              latitude: item.latitude,
              longitude: item.longitude,
              lsType: item.lsType,
              lssource: item.lssource,
              impact: item.impact,
              wea13_id: item.wea13_id,
              wea13_type: item.wea13_type,
            },
            geometry: item.coords, // This should be the actual GeoJSON geometry object
          })),
        };
        setGeoJsonData(featureCollection);
      } catch (error) {
        console.error('Error fetching geographical data:', error);
      }
    };

    fetchPoints();
  }, []);

      const pointToLayer = (feature, latlng) => {
    return L.circleMarker(latlng, {
      radius: 5, // Small circle
      fillColor: '#3388ff', // Blue color
      color: '#000',
      weight: 1,
      opacity: 1,
      fillOpacity: 0.8,
    });
  };

        const onEachFeature = (feature, layer) => {
    if (feature.properties && feature.properties.name) {
      layer.bindPopup(
        `<div>
          <strong>${feature.properties.name}</strong><br/>
          ${feature.properties.description || ''}
        </div>`
      );
    }
  };

      const displayMap = useMemo(
      () => (
          <MapContainer
          center={center}
          zoom={zoom}
          scrollWheelZoom={false}
          style={{ height: '1000px', width: '100%' }}
          >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
           <MapContent setMapInstance={setMap} />

           {geoJsonData && (
          <GeoJSON
            data={geoJsonData}
            pointToLayer={pointToLayer}
            onEachFeature={onEachFeature}
          />
        )}
          </MapContainer>
        ),
    [geoJsonData],
    )

    return (
      <div className = "MapContainerWrapper">
        {displayMap}
      </div>
    )
  }
