import React, { useState, useEffect, createContext } from 'react';
import { initializeApp } from 'firebase/app';
import {
  getAuth,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  onAuthStateChanged,
  signOut,
  signInWithCustomToken,
  signInAnonymously
} from 'firebase/auth';
import { getFirestore, doc, setDoc } from 'firebase/firestore';

// Define Firebase configuration using environment variables for local development.
// These variables are typically loaded from a .env file and MUST start with REACT_APP_
const firebaseConfig = {
  apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
  authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN,
  projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID,
  storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET,
  messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
  appId: process.env.REACT_APP_FIREBASE_APP_ID,
};

// The appId for Firestore paths can be derived from the config's projectId or a default
const appId = firebaseConfig.projectId || 'default-app-id';

// In a local development environment, __initial_auth_token is not defined.
// It's primarily for the Canvas environment's auto-authentication. We set it to null for local use.
const initialAuthToken = null; // eslint-disable-line no-undef -- This variable is for Canvas environment

// Create an AuthContext to provide authentication state to children components
export const AuthContext = createContext(null);

// AuthPage component will handle authentication UI and provide context
const AuthPage = ({ children }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState(null); // Firebase user object
  const [userId, setUserId] = useState(null); // User ID (from Firebase user or anonymous)
  const [isExplicitlyLoggedIn, setIsExplicitlyLoggedIn] = useState(false); // New state for explicit login
  const [authError, setAuthError] = useState('');
  const [authReady, setAuthReady] = useState(false); // True when Firebase auth is initialized
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [message, setMessage] = useState(''); // For success messages or general info

  // Initialize Firebase and set up authentication listener
  useEffect(() => {
    console.log('AuthPage: useEffect - Initializing Firebase...');
    console.log('AuthPage: firebaseConfig being used:', firebaseConfig); // Check what config is being used

    try {
      // Basic check if Firebase config is provided via .env
      if (!firebaseConfig.apiKey || !firebaseConfig.projectId) {
        setAuthError('Firebase configuration is missing. Please ensure your .env file is correctly set up with REACT_APP_FIREBASE_API_KEY and REACT_APP_FIREBASE_PROJECT_ID.');
        setAuthReady(true); // Mark as ready to display error
        return;
      }

      const app = initializeApp(firebaseConfig);
      const firestoreDb = getFirestore(app);
      const firebaseAuth = getAuth(app);

      setDb(firestoreDb);
      setAuth(firebaseAuth);

      // Authenticate with custom token or anonymously
      const authenticateUser = async () => {
        try {
          if (initialAuthToken) { // This block will not run in local development as initialAuthToken is null
            console.log('AuthPage: Signing in with custom token...');
            await signInWithCustomToken(firebaseAuth, initialAuthToken);
            setIsExplicitlyLoggedIn(false); // Keep false to force email/password login
          } else {
            console.log('AuthPage: Signing in anonymously (for initial Firebase setup)...');
            await signInAnonymously(firebaseAuth);
            setIsExplicitlyLoggedIn(false); // Anonymous login does NOT count as explicit login
          }
        } catch (error) {
          console.error("AuthPage: Firebase authentication failed:", error);
          setAuthError(`Authentication failed: ${error.message}`);
          setIsExplicitlyLoggedIn(false);
        } finally {
          setAuthReady(true);
          console.log('AuthPage: Authentication ready.');
        }
      };

      authenticateUser();

      // Set up auth state change listener
      const unsubscribe = onAuthStateChanged(firebaseAuth, (currentUser) => {
        console.log('AuthPage: onAuthStateChanged - currentUser:', currentUser ? currentUser.uid : 'null');
        setUser(currentUser);
        if (currentUser) {
          setUserId(currentUser.uid);
          // Only update message if it's not an explicit success/error message
          if (!message.includes('successfully') && !message.includes('failed')) {
            setMessage(`Firebase user detected. Please log in or sign up.`);
          }
        } else {
          setUserId(null);
          setIsExplicitlyLoggedIn(false); // Ensure this is false on logout
          setMessage('Please log in or sign up to access the application.');
        }
      });

      // Cleanup subscription on unmount
      return () => {
        console.log('AuthPage: Cleaning up auth state listener.');
        unsubscribe();
      };
    // eslint-disable-next-line react-hooks/exhaustive-deps
    } catch (error) {
      console.error("AuthPage: Error initializing Firebase:", error);
      setAuthError(`Firebase initialization error: ${error.message}`);
    }
  }, []); // Empty dependency array means this effect runs once on mount

  // Handle user sign up
  const handleSignUp = async () => {
    setAuthError('');
    setMessage('');
    if (!authReady || !auth) {
      setAuthError('Authentication not ready. Please wait.');
      return;
    }
    try {
      console.log('AuthPage: Attempting sign up...');
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const newUser = userCredential.user;
      if (db) {
        const userRef = doc(db, `artifacts/${appId}/users/${newUser.uid}/profile/data/userProfile`);
        await setDoc(userRef, {
          email: newUser.email,
          createdAt: new Date().toISOString(),
        });
      }
      setIsExplicitlyLoggedIn(true); // User explicitly signed up
      setMessage('Account created successfully! You are now logged in.');
      console.log('AuthPage: Sign up successful.');
    } catch (error) {
      console.error("AuthPage: Error signing up:", error);
      setAuthError(`Sign up failed: ${error.message}`);
      setIsExplicitlyLoggedIn(false);
    }
  };

  // Handle user sign in
  const handleSignIn = async () => {
    setAuthError('');
    setMessage('');
    if (!authReady || !auth) {
      setAuthError('Authentication not ready. Please wait.');
      return;
    }
    try {
      console.log('AuthPage: Attempting sign in...');
      await signInWithEmailAndPassword(auth, email, password);
      setIsExplicitlyLoggedIn(true); // User explicitly signed in
      setMessage('Logged in successfully!');
      console.log('AuthPage: Sign in successful.');
    } catch (error) {
      console.error("AuthPage: Error signing in:", error);
      setAuthError(`Sign in failed: ${error.message}`);
      setIsExplicitlyLoggedIn(false);
    }
  };

  // Handle user logout
  const handleSignOut = async () => {
    setAuthError('');
    setMessage('');
    if (!authReady || !auth) {
      setAuthError('Authentication not ready. Please wait.');
      return;
    }
    try {
      console.log('AuthPage: Attempting sign out...');
      await signOut(auth);
      setIsExplicitlyLoggedIn(false); // User logged out
      setMessage('Logged out successfully.');
      console.log('AuthPage: Sign out successful.');
    } catch (error) {
      console.error("AuthPage: Error signing out:", error);
      setAuthError(`Sign out failed: ${error.message}`);
    }
  };

  // Provide authentication state and functions via context
  const authContextValue = {
    user,
    userId,
    authReady,
    isExplicitlyLoggedIn, // Provide this new state via context
    authError,
    message,
    handleSignIn,
    handleSignUp,
    handleSignOut,
    setEmail,
    setPassword,
    email,
    password,
  };

  // Render loading state while authentication is initializing
  if (!authReady) {
    console.log('AuthPage: Rendering loading state.');
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100 p-4">
        <div className="bg-white p-8 rounded-xl shadow-lg text-center">
          <p className="text-lg text-gray-700">Loading authentication...</p>
        </div>
      </div>
    );
  }

  // If auth is ready but user is NOT explicitly logged in, display the login/signup form
  if (!isExplicitlyLoggedIn) {
    console.log('AuthPage: Rendering login/signup form.');
    return (
      <AuthContext.Provider value={authContextValue}>
        <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4 sm:p-6 lg:p-8 font-sans">
          <div className="bg-white p-6 sm:p-8 lg:p-10 rounded-2xl shadow-xl w-full max-w-md border border-gray-200">
            <h2 className="text-3xl font-extrabold text-center text-gray-800 mb-8">
              Landslide Report Login
            </h2>

            {authError && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg relative mb-6" role="alert">
                <strong className="font-bold">Error:</strong>
                <span className="block sm:inline ml-2">{authError}</span>
              </div>
            )}

            {message && (
              <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded-lg relative mb-6" role="alert">
                <strong className="font-bold">Info:</strong>
                <span className="block sm:inline ml-2">{message}</span>
              </div>
            )}

            <div>
              <div className="mb-5">
                <label htmlFor="email" className="block text-gray-700 text-sm font-bold mb-2">
                  Email:
                </label>
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="shadow-sm appearance-none border border-gray-300 rounded-lg w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-indigo-400 focus:border-transparent transition duration-200"
                  placeholder="your.email@example.com"
                />
              </div>
              <div className="mb-6">
                <label htmlFor="password" className="block text-gray-700 text-sm font-bold mb-2">
                  Password:
                </label>
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="shadow-sm appearance-none border border-gray-300 rounded-lg w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-indigo-400 focus:border-transparent transition duration-200"
                  placeholder="••••••••"
                />
              </div>
              <div className="flex flex-col space-y-4">
                <button
                  onClick={handleSignIn}
                  className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 px-6 rounded-lg shadow-md transition duration-300 ease-in-out transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-indigo-400 focus:ring-opacity-75"
                >
                  Sign In
                </button>
                <button
                  onClick={handleSignUp}
                  className="w-full bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-3 px-6 rounded-lg shadow-md transition duration-300 ease-in-out transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:ring-opacity-75"
                >
                  Sign Up
                </button>
              </div>
            </div>
          </div>
        </div>
      </AuthContext.Provider>
    );
  }

  // If user IS explicitly logged in, render the children (the rest of the app)
  console.log('AuthPage: User explicitly logged in. Rendering children.');
  return (
    <AuthContext.Provider value={authContextValue}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthPage;
