import React, { useState, useEffect, createContext, useContext } from 'react';
import { initializeApp } from 'firebase/app';
import {
  getAuth,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  onAuthStateChanged,
  signOut,
  signInWithCustomToken,
  signInAnonymously
} from 'firebase/auth';
import { getFirestore, doc, setDoc } from 'firebase/firestore';

const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

export const AuthContext = createContext(null);

const AuthPage = ({ children }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState(null);
  const [authError, setAuthError] = useState('');
  const [authReady, setAuthReady] = useState(false);
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [userId, setUserId] = useState(null);
  const [message, setMessage] = useState(''); // For success messages or general info

  // Initialize Firebase and set up authentication listener
  useEffect(() => {
    try {
      const app = initializeApp(firebaseConfig);
      const firestoreDb = getFirestore(app);
      const firebaseAuth = getAuth(app);

      setDb(firestoreDb);
      setAuth(firebaseAuth);

      // Authenticate with custom token or anonymously
      const authenticateUser = async () => {
        try {
          if (initialAuthToken) {
            await signInWithCustomToken(firebaseAuth, initialAuthToken);
          } else {
            await signInAnonymously(firebaseAuth);
          }
        } catch (error) {
          console.error("Firebase authentication failed:", error);
          setAuthError(`Authentication failed: ${error.message}`);
        } finally {
          setAuthReady(true);
        }
      };

      authenticateUser();

      const unsubscribe = onAuthStateChanged(firebaseAuth, (currentUser) => {
        setUser(currentUser);
        if (currentUser) {
          setUserId(currentUser.uid);
          setMessage(`Logged in as: ${currentUser.email || 'Anonymous'}`);
        } else {
          setUserId(null);
          setMessage('Please log in or sign up to access the application.');
        }
      });

      return () => unsubscribe();
    } catch (error) {
      console.error("Error initializing Firebase:", error);
      setAuthError(`Firebase initialization error: ${error.message}`);
    }
  }, []);

  // Handle user sign up
  const handleSignUp = async () => {
    setAuthError('');
    setMessage('');
    if (!authReady || !auth) {
      setAuthError('Authentication not ready. Please wait.');
      return;
    }
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const newUser = userCredential.user;
      if (db) {
        const userRef = doc(db, `artifacts/${appId}/users/${newUser.uid}/profile/data/userProfile`);
        await setDoc(userRef, {
          email: newUser.email,
          createdAt: new Date().toISOString(),
        });
      }
      setMessage('Account created successfully! You are now logged in.');
    } catch (error) {
      console.error("Error signing up:", error);
      setAuthError(`Sign up failed: ${error.message}`);
    }
  };

  // Handle user sign in
  const handleSignIn = async () => {
    setAuthError('');
    setMessage('');
    if (!authReady || !auth) {
      setAuthError('Authentication not ready. Please wait.');
      return;
    }
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setMessage('Logged in successfully!');
    } catch (error) {
      console.error("Error signing in:", error);
      setAuthError(`Sign in failed: ${error.message}`);
    }
  };

  const handleSignOut = async () => {
    setAuthError('');
    setMessage('');
    if (!authReady || !auth) {
      setAuthError('Authentication not ready. Please wait.');
      return;
    }
    try {
      await signOut(auth);
      setMessage('Logged out successfully.');
    } catch (error) {
      console.error("Error signing out:", error);
      setAuthError(`Sign out failed: ${error.message}`);
    }
  };

  const authContextValue = {
    user,
    userId,
    authReady,
    authError,
    message,
    handleSignIn,
    handleSignUp,
    handleSignOut,
    setEmail,
    setPassword,
    email,
    password,
  };

  if (!authReady) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100 p-4">
        <div className="bg-white p-8 rounded-xl shadow-lg text-center">
          <p className="text-lg text-gray-700">Loading authentication...</p>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <AuthContext.Provider value={authContextValue}>
        <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4 sm:p-6 lg:p-8 font-sans">
          <div className="bg-white p-6 sm:p-8 lg:p-10 rounded-2xl shadow-xl w-full max-w-md border border-gray-200">
            <h2 className="text-3xl font-extrabold text-center text-gray-800 mb-8">
              Landslide Report Login
            </h2>

            {authError && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg relative mb-6" role="alert">
                <strong className="font-bold">Error:</strong>
                <span className="block sm:inline ml-2">{authError}</span>
              </div>
            )}

            {message && (
              <div className="bg-green-100 border border-green-400 text-green-700 px-4 py-3 rounded-lg relative mb-6" role="alert">
                <strong className="font-bold">Info:</strong>
                <span className="block sm:inline ml-2">{message}</span>
              </div>
            )}

            <div>
              <div className="mb-5">
                <label htmlFor="email" className="block text-gray-700 text-sm font-bold mb-2">
                  Email:
                </label>
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="shadow-sm appearance-none border border-gray-300 rounded-lg w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-indigo-400 focus:border-transparent transition duration-200"
                  placeholder="your.email@example.com"
                />
              </div>
              <div className="mb-6">
                <label htmlFor="password" className="block text-gray-700 text-sm font-bold mb-2">
                  Password:
                </label>
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="shadow-sm appearance-none border border-gray-300 rounded-lg w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-indigo-400 focus:border-transparent transition duration-200"
                  placeholder="••••••••"
                />
              </div>
              <div className="flex flex-col space-y-4">
                <button
                  onClick={handleSignIn}
                  className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 px-6 rounded-lg shadow-md transition duration-300 ease-in-out transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-indigo-400 focus:ring-opacity-75"
                >
                  Sign In
                </button>
                <button
                  onClick={handleSignUp}
                  className="w-full bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-3 px-6 rounded-lg shadow-md transition duration-300 ease-in-out transform hover:scale-105 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:ring-opacity-75"
                >
                  Sign Up
                </button>
              </div>
            </div>
          </div>
        </div>
      </AuthContext.Provider>
    );
  }

  return (
    <AuthContext.Provider value={authContextValue}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthPage;
