import React, { useState, useEffect, createContext, useCallback, useMemo } from 'react';

const LOCAL_STORAGE_AUTH_TOKEN_KEY = 'landslide_app_auth_token';

export const AuthContext = createContext(null);

// Component renamed to AuthProvider in previous conceptual steps for clarity,
// but keeping AuthPage here as per your request to not rename the file.
const AuthPage = ({ children }) => {
  const [user, setUser] = useState(null); 
  const [userId, setUserId] = useState(null);
  const [token, setToken] = useState(null); 

  // --- CHANGED: Removed isExplicitlyLoggedIn and authReady states.
  // --- NEW: This state manages the loading during the initial authentication check on page load.
  const [isLoadingAuth, setIsLoadingAuth] = useState(true); 

  // --- CHANGED: Renamed local states for form inputs to avoid conflict with context email/password.
  const [inputEmail, setInputEmail] = useState('');
  const [inputPassword, setInputPassword] = useState('');
  // --- REMOVED: username state as it's not used by your backend /token endpoint.
  // const [username, setUsername] = useState(''); 

  const [authError, setAuthError] = useState('');
  const [message, setMessage] = useState('');

  const API_BASE_URL = 'http://127.0.0.1:8000'; 

  // --- NEW FUNCTION: Centralized helper to apply authentication data consistently ---
  // This is crucial for correctly setting/clearing all auth-related states (token, user, userId)
  // and managing localStorage in one place.
  const applyAuthData = useCallback((authToken, userData = null) => {
      setAuthError(''); // Clear any previous errors
      setMessage('');   // Clear any previous messages

      if (authToken && userData) {
          localStorage.setItem(LOCAL_STORAGE_AUTH_TOKEN_KEY, authToken);
          setToken(authToken);
          // Ensure userData has 'email' and 'id' keys matching your backend's UserResponse
          // The 'id' from backend maps to 'uid' in our frontend user object
          setUser({ email: userData.email, uid: userData.id }); 
          setUserId(userData.id); 
      } else {
          // If no token or user data is provided, it means logout or failed auth
          localStorage.removeItem(LOCAL_STORAGE_AUTH_TOKEN_KEY);
          setToken(null);
          setUser(null);
          setUserId(null); 
      }
  }, []); // No dependencies needed for this setter function


  // --- CHANGED: The primary useEffect for initial component mount/load ---
  // This useEffect now correctly checks for a stored token and triggers
  // fetchUserFromToken only if needed, managing isLoadingAuth.
  // --- REMOVED: The old useEffect that set isExplicitlyLoggedIn and authReady.
  useEffect(() => {
    const storedToken = localStorage.getItem(LOCAL_STORAGE_AUTH_TOKEN_KEY);

    // Only fetch user from token if a token is stored
    if (storedToken) {
        fetchUserFromToken(storedToken);
    } else {
        // If no token in local storage, we're done loading auth state (no user logged in)
        setIsLoadingAuth(false);
    }
  }, [fetchUserFromToken]); // fetchUserFromToken is a dependency


  // --- CHANGED: fetchUserFromToken now uses applyAuthData for state management ---
  const fetchUserFromToken = useCallback(async (authToken) => {
      if (!authToken) {
          applyAuthData(null); // Clear auth data if no token
          return;
      }

      try {
          const response = await fetch(`${API_BASE_URL}/users/me`, {
              method: 'GET',
              headers: {
                  'Authorization': `Bearer ${authToken}`,
                  'Content-Type': 'application/json',
              },
          });

          if (response.ok) {
              const data = await response.json(); 
              // CRITICAL: Ensure 'data.id' and 'data.email' exist and are strings from backend response
              if (data && typeof data.id === 'string' && typeof data.email === 'string') {
                  applyAuthData(authToken, { id: data.id, email: data.email }); // --- CHANGED: Uses applyAuthData
              } else {
                  console.error("DEBUG: /users/me response missing expected 'id' or 'email' or wrong type:", data);
                  applyAuthData(null); // --- CHANGED: Uses applyAuthData
              }
          } else {
              const errorData = await response.json();
              console.error("DEBUG: /users/me fetch failed (response.ok === false). Status:", response.status, "Details:", errorData);
              applyAuthData(null); // --- CHANGED: Uses applyAuthData
          }
      } catch (error) {
          console.error('DEBUG: Network error during /users/me fetch:', error);
          applyAuthData(null); // --- CHANGED: Uses applyAuthData
      } finally {
          setIsLoadingAuth(false); // Authentication loading is now complete
      }
  }, [API_BASE_URL, applyAuthData]); 


  // --- CHANGED: handleSignUp now calls handleSignInInternal to get a token ---
  // It no longer directly sets token/user from the /register response.
  const handleSignUp = async () => { 
    setAuthError('');
    setMessage('');
    // --- REMOVED: if (!authReady) check is no longer needed.
    if (!inputEmail || !inputPassword) { // --- CHANGED: Uses inputEmail/inputPassword
      setAuthError('Please enter both email and password.');
      return;
    }

    try {
        const response = await fetch(`${API_BASE_URL}/register`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ email: inputEmail, password: inputPassword }), // --- CHANGED: Uses inputEmail/inputPassword
        });

        if (response.ok) {
            // Registration successful. Now, immediately sign them in to get a token.
            const signInSuccess = await handleSignInInternal(inputEmail, inputPassword); // --- CHANGED: Calls internal sign-in
            if (signInSuccess) {
                setMessage('Account created successfully! You are now logged in.');
                // --- NEW: Clear form fields after successful sign up and auto-login.
                setInputEmail('');
                setInputPassword('');
            } else {
                setAuthError('Account created, but automatic login failed. Please sign in manually.');
            }
        } else {
            const errorData = await response.json();
            setAuthError(errorData.detail || 'Sign up failed.');
        }
    } catch (error) {
        console.error('Sign up network error:', error);
        setAuthError('Network error. Please try again.');
    }
  };

  // --- NEW FUNCTION: Internal helper for Sign In logic ---
  // This function performs the actual sign-in API call and state update.
  const handleSignInInternal = async (signInEmail, signInPassword) => { 
    setAuthError('');
    setMessage('');
    if (!signInEmail || !signInPassword) {
      setAuthError('Please enter both email and password.');
      return false; 
    }

    try {
        const details = new URLSearchParams();
        details.append('username', signInEmail); 
        details.append('password', signInPassword);

        const response = await fetch(`${API_BASE_URL}/token`, { 
            method: 'POST',
            headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
            body: details.toString(),
        });

        if (response.ok) {
            const data = await response.json(); 
            // CRITICAL: Ensure 'data.user_id' matches backend /token response
            applyAuthData(data.access_token, { id: data.user_id, email: data.email }); // --- CHANGED: Uses applyAuthData
            setMessage('Logged in successfully!');
            // --- NEW: Clear form fields after successful login
            setInputEmail('');
            setInputPassword('');
            return true; // Indicate success
        } else {
            const errorData = await response.json();
            setAuthError(errorData.detail || 'Invalid email or password.');
            return false; 
        }
    } catch (error) {
        console.error('Sign in network error:', error);
        setAuthError('Network error. Please try again.');
        return false; 
    }
  };

  // --- CHANGED: Public handleSignIn calls the internal helper ---
  const handleSignIn = async () => {
    await handleSignInInternal(inputEmail, inputPassword); // --- CHANGED: Calls internal helper
  };

  // --- CHANGED: handleSignOut now uses applyAuthData to clear state ---
  const handleSignOut = useCallback(async () => { 
    setAuthError('');
    setMessage('Logged out successfully.');
    applyAuthData(null); // --- CHANGED: Uses applyAuthData
  }, [applyAuthData]);

  // --- CHANGED: authContextValue dependencies and exposed properties ---
  // useMemo ensures context only re-renders consumers when its value actually changes.
  const authContextValue = useMemo(() => ({
    user,
    userId, 
    token,  
    isLoadingAuth, // --- EXPOSED: Loading state is crucial for App.js
    authError,
    message,
    handleSignIn,
    handleSignUp,
    handleSignOut,
    // --- REMOVED: email/password states are now local to the form rendering logic, not exposed via context.
    // email,
    // password,
    // --- REMOVED: authReady and isExplicitlyLoggedIn are removed.
    // authReady,
    // isExplicitlyLoggedIn,
  }), [user, userId, token, isLoadingAuth, authError, message, handleSignIn, handleSignUp, handleSignOut]);


  // --- CHANGED: Render Logic for AuthPage ---
  // Now uses isLoadingAuth and 'token' (instead of isExplicitlyLoggedIn/authReady) to control display.

  // Show a loading screen while authentication status is being determined
  if (isLoadingAuth) {
    return (
      <div className="auth-loading-container">
        <div className="auth-loading-box">
          <p className="auth-loading-text">Loading authentication...</p>
        </div>
      </div>
    );
  }

  // If not authenticated (token is null/undefined) AND not loading, render the Login/Signup Form.
  // This will be the first thing the user sees if they are not logged in.
  if (!token) { // --- CHANGED: Condition uses 'token' directly
    return (
      <AuthContext.Provider value={authContextValue}>
        <div className="auth-page-container">
          <div className="auth-form-card">
            <h2 className="auth-form-title">
              Landslide Report Login
            </h2>

            {authError && (
              <div className="auth-error-message" role="alert">
                <strong className="auth-message-strong">Error:</strong>
                <span className="auth-message-span">{authError}</span>
              </div>
            )}

            {message && (
              <div className="auth-info-message" role="alert">
                <strong className="auth-message-strong">Info:</strong>
                <span className="auth-message-span">{message}</span>
              </div>
            )}

            {/* Login Form Inputs - now part of AuthPage's render */}
            <div>
              {/* --- REMOVED: 'username' input field entirely. */}
              {/* <div className="auth-input-group">
                <label htmlFor="username" className="auth-label">Username:</label>
                <input type="username" id="username" value={username} onChange={(e) => setUsername(e.target.value)} className="auth-input" placeholder="your_username"/>
              </div> */}

              <div className="auth-input-group">
                <label htmlFor="email" className="auth-label">
                  Email:
                </label>
                <input
                  type="email" 
                  id="email"
                  value={inputEmail} // --- CHANGED: Uses local inputEmail state
                  onChange={(e) => setInputEmail(e.target.value)} // --- CHANGED: Updates local inputEmail state
                  className="auth-input"
                  placeholder="your.email@example.com"
                />
              </div>
              <div className="auth-input-group">
                <label htmlFor="password" className="auth-label">
                  Password:
                </label>
                <input
                  type="password"
                  id="password"
                  value={inputPassword} // --- CHANGED: Uses local inputPassword state
                  onChange={(e) => setInputPassword(e.target.value)} // --- CHANGED: Updates local inputPassword state
                  className="auth-input"
                  placeholder="••••••••"
                />
              </div>

              <div className="auth-button-group">
                <button
                  onClick={handleSignIn} 
                  className="auth-button auth-button-primary"
                >
                  Sign In
                </button>
                <button
                  onClick={handleSignUp} 
                  className="auth-button auth-button-secondary"
                >
                  Sign Up
                </button>
              </div>
            </div>
          </div>
        </div>
      </AuthContext.Provider>
    );
  }

  // If authenticated (token is present and isLoadingAuth is false), render children
  return (
    <AuthContext.Provider value={authContextValue}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthPage;





